    private void StartGenerateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_StartGenerateActionPerformed
        String lengthKey = enterLegthKey.getText();
        String complicatedKey = enterComplicatedKey.getText();
        
            Integer intlengthKey = 0;
            Integer intcomplicatedKey = 0;
            try { 
                intlengthKey = new Integer(lengthKey);
                intcomplicatedKey = new Integer(complicatedKey);
            }catch (NumberFormatException e) {  
                System.err.println("Неверный формат строки!");  
            }
            
        ThisIsGenerateKey.setText(testwork4.Generate.Key(intlengthKey, intcomplicatedKey));
    }//GEN-LAST:event_StartGenerateActionPerformed

