    private void ButtonConfirmActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonConfirmActionPerformed
        // EnterLine
        String line = EnterLine.getText();
        output_encoding_Base64.setText(testwork4.HandlerKey.encodedKeyBase64(line));
        output_encrypt_sha1.setText(testwork4.PasswordEncrypter.getProtectedKeySHA_1(line));
        output_encrypt_md5.setText(testwork4.PasswordEncrypter.getProtectedKeyMD_5(line));
        output_ecodingBase64_sha1.setText(testwork4.PasswordEncrypter.bufferKey2(testwork4.PasswordEncrypter.getProtectedKeySHA_1(line), "sha-1"));
        output_encodingBase64_md5.setText(testwork4.PasswordEncrypter.bufferKey2(testwork4.PasswordEncrypter.getProtectedKeyMD_5(line), "md5"));
    }//GEN-LAST:event_ButtonConfirmActionPerformed

